import { useEffect, useState } from "react";
import axios from "axios";
import { Button, TextField, Grid } from "@material-ui/core";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { apiUrl } from "../config.json";

export default function User({
  location: {
    state: { firstName, lastName, id },
  },
}) {
  const [projects, setProjects] = useState([{ id: 0 }]);
  const [selectedProject, setSelectedProject] = useState({});
  const [input, setInput] = useState("");
  const [update, setUpdate] = useState(false);

  useEffect(() => {
    const getProjects = async () => {
      const { data } = await axios.get(`${apiUrl}/users/${id}/projects`);
      const projects = [
        { id: 1, projectname: "Oliver Hansen" },
        { id: 2, projectname: "Van Henry" },
      ];
      setProjects(data);
      setUpdate(false);
    };
    getProjects();
  }, [update]);

  const handleSelect = (event) => {
    const {
      target: { value },
    } = event;
    console.log(value);
    setSelectedProject(value);
  };

  const handleDelete = async () => {
    // This is for test-case. Later delete console.log and uncomment axios part
    console.log(selectedProject.id);
    const sessions = await axios.get(`${apiUrl}/users/${id}/projects/${selectedProject.id}/sessions`);
    if (sessions.data.length  == 0) {
      const res = await axios.delete(`${apiUrl}/users/${id}/projects/${selectedProject.id}`);
      if (res.status == 200) {
        alert("Project \"" + res.data.projectname + "\" is successfully deleted.");
      }
    } else {
      render(() => {
        return (
          <div>
            <Modal
              content={this.renderContentProp()}   
              header="Delete this?"                
              actions={this.renderActionButtons()}
              onDismiss={this.onDismiss}
            />
          </div>
        );
      });
    }
    setSelectedProject({});
    setUpdate(true);
  };

  renderActionButtons = () => {
    //return JSX that renders action buttons...
    return (
      <div>
        <div className="ui button primary">Delete</div>
        <div className="ui button">Cancel</div>
      </div>
    );
  };

  const handleInput = (event) => {
    const {
      target: { value },
    } = event;
    setInput(value);
  };

  const handleCreate = async () => {
    await axios.post(`${apiUrl}/users/${id}/projects`, {
      projectname: input,
    });
    setInput("");
    setUpdate(true);
  };

  const gridStyle = {
    padding: 20,
    height: "60vh",
    width: 500,
    margin: "20px auto",
  };

  return (
    <div style={{ margin: 20 }}>
      <h1>Manage Projects</h1>
      <Grid style={gridStyle}>
        <FormControl sx={{ width: 200}}>
          <InputLabel>Project</InputLabel>
          <Select
            value={selectedProject}
            onChange={handleSelect}
            input={<OutlinedInput label="Name" />}
          >
            {projects.map((project) => (
              <MenuItem key={project.id} value={project}>
                {project.projectname}
              </MenuItem>
            ))}
          </Select>
          <Button variant="contained" onClick={handleDelete}>
            Delete
          </Button>
        </FormControl>

        <FormControl style={{marginLeft:30}}>
          <TextField
            id="outlined-basic"
            label="New Project"
            variant="outlined"
            onChange={handleInput}
            value={input}
          />
          <Button variant="contained" onClick={handleCreate}>
            Create
          </Button>
        </FormControl>
      </Grid>
    </div>
  );
}
